import { Component, OnInit } from '@angular/core';
import {ProductSaleService} from '../../services/product-sale.service';
import {ShopManagerService} from '../../services/shop-manager.service';
import * as pdfMake from 'pdfmake/build/pdfmake';
import pdfFonts from 'pdfmake/build/vfs_fonts';
pdfMake.vfs = pdfFonts.pdfMake.vfs;

@Component({
  selector: 'app-shop-vat-report',
  templateUrl: './shop-vat-report.component.html',
  styleUrls: ['./shop-vat-report.component.sass']
})
export class ShopVatReportComponent implements OnInit {
  sales: any[];
  shop: any;
  totalTax = 0;

  constructor(
    private productService: ProductSaleService,
    private shopManager: ShopManagerService,
    private salesService: ProductSaleService
  ) {
    shopManager.getShopSaved().subscribe((shop) => {
      this.shop = shop;
    });
  }

  ngOnInit(): void {
    this.getSales();
  }

  getSales(): void {
    this.salesService.getSales(this.shop.id).subscribe((response: any[]) => {
      response.forEach((sale) => {
        sale.date = new Date(sale.date).toLocaleDateString('en-GB');
        sale.totalTax = sale.saleAmount * 0.16;
      });
      this.sales = response;
      this.totalTax = this.getTotalTax(this.sales);
    });
  }

  printSalesReport(sales: any[]): void {
    const detailed = true;
    const documentDefinition = {
      content: [
        {
          image: 'data:image/png;base64,',
          width: 100
        },
        {
          columns: [
            [
              { text: `Date - ${new Date(Date.now()).toLocaleDateString()}`, style: 'textRegular'},
              {text: 'Weaverbirds Limited', style: 'textRegular'},
              {text: 'P.O. Box 456-90100', style: 'textRegular'},
              {
                text: 'Machakos',
                fontSize: 8,
                style: {
                  margin: [0, 10, 0, 20]
                }
              }
            ],
          ]
        },
        {
          text: `Sales Report - ${this.shop.name}`,
          bold: true,
          style: 'subHeading'
        },
        {
          table: {
            headerRows: 1,
            widths: [120, 120 , 120 , 120],
            heights: (row) => {
              if (row === 0) {
                return 0;
              } else {
                return 17;
              }
            },
            body: [
              ['Date', 'Receipt No.', 'Mode of Payment', 'Sub Total'],
              ...sales.map((p => (
                [
                  p.date, p.receiptNumber, p.paymentMethod,
                  p.saleAmount.toFixed(2)
                ]))),
              [
                {
                  text: 'Total', fontSize: 8, bold: true, colspan: 6
                }, {}, {}, this.getSalesTotalAmount(sales).toFixed(2)
              ]
            ]
          },
          layout: 'headerLineOnly',
          fontSize: 7
        },
        {
          text: '',
          margin: [0, 15, 0, 15]
        },
        {
          columns: [
            [
              {text: '', margin: [0, 10, 0, 10]},
              {text: 'Confirmed and signed by:\n\nName ...................................................', style: 'textRegular'},
              {text: 'Signature ..............................................', style: 'textRegular'}
            ]
          ]
        }
      ],
      styles: {
        sectionHeader: {
          margin: [0, 10, 0, 10],
          decoration: 'underline',
          bold: true
        },
        subHeading: {
          margin: [0, 20, 0, 10],
          fontSize: 10
        },
        textRegular: {
          margin: [0, 5, 0, 5],
          fontSize: 8
        }
      }
    };
    const win = window.open('', 'tempWinForPdf');
    pdfMake.createPdf(documentDefinition).print({silent: false}, win); // print the table data
    // win.close();
  }

  private getSalesTotalAmount(productSales: any[]): number {
    let totalSale = 0;
    productSales.forEach((productSale) => {
      totalSale += productSale.sellingPrice * productSale.quantity;
    });
    return totalSale;
  }

  filterProductSales(value: any): void {

  }

  updateTaxSales($event: any[]): void {
    this.sales = $event;
    this.totalTax = this.getTotalTax(this.sales);
  }

  private getTotalTax(sales: any[]): number {
    let totalTax = 0;
    sales.forEach((sale) => {
      totalTax += sale.totalTax;
    });
    return totalTax;
  }
}
